import { useState } from "react";
import '../Css/Drawer.css';
import {useSelector,useDispatch} from 'react-redux';
import { setListData } from "../Redux/Action";
function AddDataInList(props)
{
    const dispatch=useDispatch();
  // const  [iteam,setIteam]=useState([]);
   const  [inputData,setInputData]=useState("");
   const handleAddIteam=(event)=>{
    var oldIteam=useSelector(state=>state.iteam)
    dispatch(setListData(oldIteam));
    //iteam.push(inputData);
    }
    const handleInputChange=(data)=>{
        setInputData(data);
       // console.log(iteam);
    }

    const listIteam=()=>
    {
        let Iteams=useSelector(state=>state.iteam);
        let listIteam=Iteams.map((value)=>
        <li key={value}>{value}</li>
        )
            return(<ol>{listIteam}</ol>)
    }
    return(
        <>
        <div className="addItem">
            <h3>{props.name}</h3>
            <div className="inputContent">
                <input type="text"onChange={(e)=>handleInputChange(e.target.value)}/>
                <button  onClick={()=>handleAddIteam()}>Add</button>
            </div>
            <div>
                {listIteam()}
            </div>
        </div>
        </>
    )
}

export default AddDataInList
