import { useState } from "react";
import '../Css/Drawer.css';
import {useSelector,useDispatch} from 'react-redux';
import { setListData,setCounter } from "../Redux/Action";
function AddDataInList(props)
{
    const dispatch=useDispatch();
    const count=useSelector(state=>state.counter);
    const listData=useSelector(state=>state.iteam);
    const  [inputData,setInputData]=useState("");

    const handleAddIteam=(event)=>{
    var oldIteam=listData;
        oldIteam.push({id:count+1,value:inputData})
        dispatch(setListData(oldIteam));
        dispatch(setCounter(count+1));
        setInputData("");
    }

    const handleRemoveIteam=(event)=>{
        var remaningList=listData.filter(x=>x.id!==event.id);
            
            dispatch(setListData(remaningList));
            //dispatch(setCounter(count+1));
            setInputData("");
        }
    const handleInputChange=(data)=>{
        setInputData(data);
    }

    const listIteam=()=>
    {
        //let Iteams=useSelector(state=>state.iteam);
        let listIteam=listData.map((e)=>
        <li key={e.id}>{e.value}</li>
        )
            return(<ol>{listIteam}</ol>)
    }
    return(
        <>
        <div className="addItem">
            <h3>{props.name}</h3>
            <div className="inputContent">
                <input type="text"onChange={(e)=>handleInputChange(e.target.value)}/>
                <button  onClick={()=>handleAddIteam()}>Add Task</button>
                <button  onClick={()=>handleAddIteam()}>Remove Task</button>
            </div>
            <div>
                {listIteam()}
            </div>
        </div>
        </>
    )
}

export default AddDataInList
